func TestRun(t *testing.T) {
	actual := Decompress(compressed)

	if len(decompressed) != len(actual) {
		t.Fatalf("TestRun failed: wrong size of decompressed data. want=%d, got=%d", len(decompressed), len(actual))
	}

	for i, b := range decompressed {
		if b != actual[i] {
			t.Fatalf("TestRun failed: offset:%d's data is wrong in decompressed data. want=0x%02x, got=%02x", i, b, actual[i])
		}
	}
}
